import { useState } from "react";
import { Card } from "./component/card";
import "./App.css";
import { useEffect } from "react";

function App() {
  const item = [
    {symbol:"1", }, 
    {symbol:"2",}, 
    {symbol:"3",}, 
    {symbol:"4",}, 
    {symbol:"5",}, 
    {symbol:"6",}, 
    {symbol:"7",}, 
    {symbol:"8",}
  ];
  const [cards, setCards] = useState({ item });
  const [count, setCount] = useState();
  function resetCards() {
    let shuffled = [...item, ...item].sort(() => Math.random() - 0.5);
    console.log(shuffled);
    setCards(shuffled);
  }
  useEffect(() => {
    resetCards();
  }, []);
  return (
    <div className="App">
      <div className="wrapper">
        {Array.from(cards).map((i, idx) => (
          <Card key={idx} emoji={i} />
        ))}
      </div>
    </div>
  );
}

export default App;
