import { useState } from "react";
import { Card } from "./component/card";
import "./App.css";
import { useEffect } from "react";

function App() {
  const item = [
    { symbol: "1", id: 1 },
    { symbol: "2", id: 2 },
    { symbol: "3", id: 3 },
    { symbol: "4", id: 4 },
    { symbol: "5", id: 5 },
    { symbol: "6", id: 6 },
    { symbol: "7", id: 7 },
    { symbol: "8", id: 8 },
  ];
  const [cards, setCards] = useState({ item });
  const [count, setCount] = useState();
  function resetCards() {
    let shuffled = [...item, ...item].sort(() => Math.random() - 0.5);

    setCards({ item: shuffled });
  }
  useEffect(() => {
    resetCards();
  }, []);
  console.log(cards);
  return (
    <div className="App">
      <div className="wrapper">
        {Array.from(cards).map((i, idx) => {
          <Card key={idx} emoji={i.symbol} />;
        })}
      </div>
    </div>
  );
}

export default App;
