import { useState } from "react";
import { Card } from "./component/card";
import "./App.css";
import { useEffect } from "react";

function App() {
  const item = [
    { symbol: "1", id: 1, matchFound: false },
    { symbol: "2", id: 2, matchFound: false },
    { symbol: "3", id: 3, matchFound: false },
    { symbol: "4", id: 4, matchFound: false },
    { symbol: "5", id: 5, matchFound: false },
    { symbol: "6", id: 6, matchFound: false },
    { symbol: "7", id: 7, matchFound: false },
    { symbol: "8", id: 8, matchFound: false },
  ];
  const [cards, setCards] = useState({ item });
  const [firstC, setFirstC] = useState(null);
  const [disabled, setDisabled] = useState(false);
  const [secondC, setSecondC] = useState(null);
  const [count, setCount] = useState(0);
  const [moves, setMoves] = useState(0);
  function resetCards() {
    let shuffled = [...item, ...item].sort(() => Math.random() - 0.5);
    setCards({ item: shuffled });
  }
  function resetChoices() {
    setFirstC(null);
    setSecondC(null);
    setMoves((pre) => pre + 1);
    setDisabled(false);
  }
  function handleSelection(e) {
    firstC ? setSecondC(e.target.dataset.id) : setFirstC(e.target.dataset.id);
  }
  useEffect(() => {
    resetCards();
  }, []);
  useEffect(() => {
    setDisabled(true);
    if (!secondC) {
      return;
    }
    if (firstC === secondC) {
      setCount((pre) => pre + 1);
      setCards((pre) => {
        Array.from(pre.item).map((c) => {
          if (c.id === firstC) {
            return { ...c, matchFound: true };
          } else {
            return c;
          }
        });
        resetChoices();
      });
      if (count === 8) {
        return alert("恭喜！你完成了游戏！");
      }
    }
    resetChoices();
  }, [firstC, secondC]);

  return (
    <div className="App">
      <div className="wrapper">
        {Array.from(cards.item).map(({ symbol, id }, idx) => {
          return (
            <Card
              key={idx}
              emoji={symbol}
              id={id}
              disabled={disabled}
              handleSelection={handleSelection}
            />
          );
        })}
      </div>
    </div>
  );
}

export default App;
