import { useState } from "react";
import { Card } from "./component/card";
import "./App.css";
import { useEffect } from "react";

function App() {
  const item = [
    { symbol: "1", id: 1 },
    { symbol: "2", id: 2 },
    { symbol: "3", id: 3 },
    { symbol: "4", id: 4 },
    { symbol: "5", id: 5 },
    { symbol: "6", id: 6 },
    { symbol: "7", id: 7 },
    { symbol: "8", id: 8 },
  ];
  const [cards, setCards] = useState({ item });
  const [firstC, setFirstC] = useState(null);
  const [secondC, setSecondC] = useState(null);
  //const [count, setCount] = useState();
  function resetCards() {
    let shuffled = [...item, ...item].sort(() => Math.random() - 0.5);
    setCards({ item: shuffled });
  }

  useEffect(() => {
    resetCards();
  }, []);
  function handleSelection(e) {
    secondC ? setFirstC():
    firstC ? setSecondC(e.target.dataset.id) : setFirstC(e.target.dataset.id);
    console.log(e.target.dataset.id);
  }
  return (
    <div className="App">
      <div className="wrapper">
        {Array.from(cards.item).map(({ symbol, id }, idx) => {
          return (
            <Card
              key={idx}
              emoji={symbol}
              id={id}
              handleSelection={handleSelection}
            />
          );
        })}
      </div>
    </div>
  );
}

export default App;
