import { useState } from "react";
import { Card } from "./component/card";
import "./App.css";
import { useEffect } from "react";

function App() {
  const item = [
    { emoji: "1", id: 1, matchFound: false },
    { emoji: "2", id: 2, matchFound: false },
    { emoji: "3", id: 3, matchFound: false },
    { emoji: "4", id: 4, matchFound: false },
    { emoji: "5", id: 5, matchFound: false },
    { emoji: "6", id: 6, matchFound: false },
    { emoji: "7", id: 7, matchFound: false },
    { emoji: "8", id: 8, matchFound: false },
  ];
  const [cards, setCards] = useState(item);
  const [firstC, setFirstC] = useState(null);
  const [disabled, setDisabled] = useState(false);
  const [secondC, setSecondC] = useState(null);
  const [count, setCount] = useState(0);
  const [moves, setMoves] = useState(0);
  function resetCards() {
    let shuffled = [...item, ...item].sort(() => Math.random() - 0.5);
    setCards(shuffled);
  }
  function resetChoices() {
    setFirstC(null);
    setSecondC(null);
    setMoves((pre) => pre + 1);
    setDisabled(false);
  }
  function handleSelection(e) {
    firstC ? setSecondC(e.target.dataset.id) : setFirstC(e.target.dataset.id);
  }
  function resetGame (){
    setCount(0)
    setMoves(0)
    resetCards()
  }
  useEffect(() => {
    resetCards();
  }, []);
  useEffect(() => {
    //setDisabled(true);
    if (!secondC) {
      return;
    }
    if (firstC === secondC) {
      setCount((pre) => {
        if (pre+1 == 8) {
          setTimeout(() => {
            alert("you win");
            return resetGame()
          }, 100);
        }
        return pre + 1
      });
      setCards((pre) => {
        return pre.map((c) => {
          if (c.id == firstC) {
            return { ...c, matchFound: true };
          } else {
            return c;
          }
        });
        //resetChoices();
      });
    }
    resetChoices();
  }, [firstC, secondC]);
  return (
    <div className="App">
      <p>Total Moves:${}</p>
      <div className="wrapper">
        {Array.from(cards).map((card, idx) => {
          return (
            <Card
              key={idx}
              card={card}
              disabled={disabled}
              handleSelection={handleSelection}
            />
          );
        })}
      </div>
    </div>
  );
}

export default App;
